<div class="wrap sks"><div class="icon32" id="icon-options-general"><br></div><h2>WP RoundAbout Pro Sliders</h2><br /><table class="form-table"><tr><th style="width:40px;">ID</th><th>Title</th><th style="width:50%;">Description</th><th>Shortcode</th><th>Options</th></tr><?phpif($_POST[title]!='' && trim($_POST[title])!=' '){$title = htmlentities($_POST[title],ENT_COMPAT,'utf-8');$description = htmlentities($_POST[description],ENT_COMPAT,'utf-8');$type = $_POST[type];$today = time();global $wpdb;if($type=='Simple Roundabout 3D slider'){$wpdb->query("INSERT INTO `".$wpdb->prefix."rbt_sliders` (`id`, `title`, `date`, `description`, `type`, `opt1`, `opt2`, `opt3`, `opt4`, `opt5`, `opt6`, `opt7`, `opt8`, `opt9`, `opt10`, `opt11`, `opt12`, `opt13`) VALUES (NULL, '$title', '$today', '$description', '$type', '480', '320', 'x', '#eeeeee', '480', '320', 'true', 'true', '2', '0.1', '0.9', 'false', 'true')"); } else {$wpdb->query("INSERT INTO `".$wpdb->prefix."rbt_sliders` (`id`, `title`, `date`, `description`, `type`, `opt1`, `opt2`, `opt3`, `opt4`, `opt5`) VALUES (NULL, '$title', '$today', '$description', '$type', '900', '400', '1', '#000000', '#000000')");}$inserted = $wpdb->insert_id;echo'<script>document.location.href="admin.php?page=rbt-settings.php&options='.$inserted.'";</script>';}if(isset($_GET[delete])){$wpdb->query("DELETE FROM `".$wpdb->prefix."rbt_sliders` WHERE `id` = '$_GET[delete]'");$wpdb->query("DELETE FROM `".$wpdb->prefix."rbt_slides` WHERE `sliderid` = '$_GET[delete]'");echo'<script>document.location.href="admin.php?page=rbt-settings.php";</script>';}$results = $wpdb->get_results("SELECT * FROM `".$wpdb->prefix."rbt_sliders`", ARRAY_A);$i=0;if ($results){	foreach ($results as $row) {		echo'<tr><td>'.$row[id].'</td><td>'.$row[title].'</td>		<td>'.$row[description].'<h5>Created on '.date("l, d-m-Y, h:i a", $row['date']).'</h5><h5>'.$row['type'].'</h5></td>		<td>[wprbt slider_id="'.$row[id].'"]</td><td>		<a title="Manage slides" href="admin.php?page=rbt-settings.php&edit='.$row[id].'"><img src="'.plugins_url( 'images/slides.png', __FILE__ ).'" /></a>		<a title="Change slider settings" href="admin.php?page=rbt-settings.php&options='.$row[id].'"><img src="'.plugins_url( 'images/settings.png', __FILE__ ).'" /></a>		<a title="Delete this slider" onclick="del_rbts('.$row[id].')" href="#"><img src="'.plugins_url( 'images/delete.png', __FILE__ ).'" /></a>		</td></tr>';		++$i;	}}if($i==0){echo'<tr><td colspan="5"><i>There are no sliders present. Create a slider to continue.</i></td></tr>';}?></table><form action="" method="post"><h2>&nbsp;</h2><h2>Create new slider</h2><table class="sks-table"><tr><td>Title</td><td><input type="text" name="title" /></td></tr><tr><td>Description</td><td><textarea rows="4" name="description"></textarea></td></tr><tr><td>Slider Type</td><td><select name="type"><option>Simple Roundabout 3D slider</option><option>Facade Slideshow</option></select></td></tr></table><p class="submit"><input type="submit" value="Save Changes" class="button button-primary" id="submit" name="submit"></p></form></div>